import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './AdminPage.css';
import toast, { Toaster } from 'react-hot-toast';

function Admin() {
  const [jobs, setJobs] = useState([]);
  const [applications, setApplications] = useState([]);
  const adminId = localStorage.getItem('adminId'); // Replace this with the actual admin ID

  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const response = await axios.get(`http://127.0.0.1:3002/api/admin/getAllJobs/${adminId}`);
        setJobs(response.data.jobs);
      } catch (error) {
        console.error('Error fetching jobs:', error);
        toast.error('Error fetching jobs');
      }
    };

    const fetchApplications = async () => {
      try {
        const response = await axios.get(`http://127.0.0.1:3002/api/admin/getAllApplications/${adminId}`);
        setApplications(response.data.applications);
      } catch (error) {
        console.error('Error fetching applications:', error);
        toast.error('Error fetching applications');
      }
    };

    fetchJobs();
    fetchApplications();
  }, [adminId]);

  const handleCreateJob = async (jobData) => {
    try {
      const response = await axios.post('http://127.0.0.1:3002/api/admin/createJob', jobData);
      if (response.status === 201) {
        setJobs([...jobs, response.data.job]);
        toast.success('Job created successfully');
      }
    } catch (error) {
      toast.error('Error creating job');
      console.error('Error creating job:', error);
    }
  };

  return (
    <div className="container">
      <main className="content">
        <h1>Admin Dashboard</h1>
        <section>
          <h2>Job Listings</h2>
          <ul>
            {jobs.map((job) => (
              <li key={job._id} className="job-item">
                <h2>{job.title}</h2>
                <p>{job.position}</p>
                <p>{job.company}</p>
                <p>{job.requirements}</p>
              </li>
            ))}
          </ul>
        </section>
        <section>
          <h2>Applications</h2>
          <ul>
            {applications.map((app) => (
              <li key={app._id} className="application-item">
                <h2>{app.job.title}</h2>
                <p>Applicant: {app.applicant.name}</p>
                <p>Resume: {app.applicant.resume}</p>
                <p>Status: {app.status}</p>
              </li>
            ))}
          </ul>
        </section>
        <section>
          <h2>Create Job</h2>
          <form onSubmit={handleCreateJob}>
            <input type="text" name="title" placeholder="Job Title" required />
            <input type="text" name="position" placeholder="Position" required />
            <input type="text" name="company" placeholder="Company" required />
            <textarea name="requirements" placeholder="Requirements" required></textarea>
            <button type="submit">Create Job</button>
          </form>
        </section>
      </main>
      <Toaster />
    </div>
  );
}

export default AdminPage;
