import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './JobHome.css';
import toast, { Toaster } from 'react-hot-toast';

function JobHome() {
  const [jobs, setJobs] = useState([]);
  const [appliedJobs, setAppliedJobs] = useState(new Set()); // To track applied jobs
  const [selectedStatus, setSelectedStatus] = useState({}); // To track selected status for each job
  const applicantId = localStorage.getItem('id'); // Replace this with the actual applicant ID

  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:3002/api/admin/getAllJobsForUser');
        setJobs(response.data.jobs); 
      } catch (error) {
        toast.error('Error fetching jobs');
      }
    };

    fetchJobs();
  }, []);

  const handleApply = async (jobId) => {
    // Ensure applicantId is present
    if (!applicantId) {
      toast.error('Applicant ID is missing');
      return;
    }
    
    const status = selectedStatus[jobId] || 'Pending'; // Default to 'Pending' if no status selected

    try {
      // Send POST request with jobId, applicantId, and status
      const response = await axios.post(`http://127.0.0.1:3002/api/user/apply/${jobId}`, { applicantId, status });
      
      console.log('API Response:', response); // Log API response for debugging
      
      if (response.status === 200) {
        // Update state to reflect the applied job
        setAppliedJobs((prev) => new Set(prev).add(jobId));
        toast.success('Applied successfully');
      } else {
        toast.error(response.data.message || 'Error applying for job');
      }
    } catch (error) {
      console.error('API Error:', error); // Log error for debugging
      toast.error(error.response?.data?.message || 'Error applying for job');
    }
  };

  const handleStatusChange = (jobId, status) => {
    setSelectedStatus((prev) => ({ ...prev, [jobId]: status }));
  };

  return (
    <div className="container">
      <main className="content">
        <h1>Job Listings</h1>
        <ul>
          {jobs.map((job) => (
            <li key={job._id} className="job-item">
              <h2>{job.title}</h2>
              <p>{job.position}</p>
              <p>{job.company}</p>
              <p>{job.requirements}</p>
              <select
                onChange={(e) => handleStatusChange(job._id, e.target.value)}
                disabled={appliedJobs.has(job._id)}
                value={selectedStatus[job._id] || 'Pending'}
              >
                <option value="Pending">Pending</option>
                <option value="In Review">In Review</option>
                <option value="Accepted">Accepted</option>
                <option value="Rejected">Rejected</option>
              </select>
              <button
                className={`apply-button ${appliedJobs.has(job._id) ? 'applied' : ''}`}
                onClick={() => handleApply(job._id)}
                disabled={appliedJobs.has(job._id)} // Disable button if already applied
              >
                {appliedJobs.has(job._id) ? 'Applied' : 'Apply for this Job'}
              </button>
            </li>
          ))}
        </ul>
      </main>
      <Toaster /> {/* This will render toast notifications */}
    </div>
  );
}

export default JobHome;
