import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Admin.css';
import toast, { Toaster } from 'react-hot-toast';

function Admin() {
  const [jobs, setJobs] = useState([]);
  const [activeDropdown, setActiveDropdown] = useState(null); // Track active dropdown
  const adminId = localStorage.getItem('id'); // Replace this with the actual admin ID

  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const response = await axios.get(`http://127.0.0.1:3002/api/admin/getAllJobs/${adminId}`);
        setJobs(response.data.jobs);
      } catch (error) {
        console.error('Error fetching jobs:', error);
        toast.error('Error fetching jobs');
      }
    };

    fetchJobs();
  }, [adminId]);

  const handleCreateJob = async (event) => {
    event.preventDefault();
    const { title, position, company, requirements } = event.target.elements;
    const jobData = {
      title: title.value,
      position: position.value,
      company: company.value,
      requirements: requirements.value,
    };
  
    try {
      const response = await axios.post(`http://127.0.0.1:3002/api/admin/postjob/${adminId}`, jobData);
      if (response.status === 200) {
        setJobs(prevJobs => [...prevJobs, response.data.job]);
        toast.success('Job posted successfully');
      } else {
        toast.error('Failed to post job');
      }
    } catch (error) {
      console.error('Error posting job:', error.response ? error.response.data : error.message);
      toast.error('Error posting job');
    }
  };

  const handleDropdownToggle = (jobId) => {
    setActiveDropdown(activeDropdown === jobId ? null : jobId);
  };

  return (
    <div className="container">
      <main className="content">
        <h1>Admin Dashboard</h1>
        <section>
          <h2>Job Listings with Applicants</h2>
          <ul>
            {jobs?.map((job) => (
              <li key={job?._id} className="job-item">
                <h2>{job?.title}</h2>
                <p>{job?.position}</p>
                <p>{job?.company}</p>
                <p>{job?.requirements}</p>
                <button
                  className="applicants-dropdown-button"
                  onClick={() => handleDropdownToggle(job._id)}
                >
                  Show Applicants
                </button>
                <div className={`applicants-dropdown-content ${activeDropdown === job._id ? 'active' : ''}`}>
                  {job?.applicants?.length ? (
                    job.applicants.map(applicant => (
                      <a key={applicant._id} href={`mailto:${applicant.email}`}>
                        {applicant.name} ({applicant.email})
                      </a>
                    ))
                  ) : (
                    <p>No applicants</p>
                  )}
                </div>
              </li>
            ))}
          </ul>
        </section>
        <section>
          <h2>Create Job</h2>
          <form onSubmit={handleCreateJob}>
            <input type="text" name="title" placeholder="Job Title" required />
            <input type="text" name="position" placeholder="Position" required />
            <input type="text" name="company" placeholder="Company" required />
            <textarea name="requirements" placeholder="Requirements" required></textarea>
            <button type="submit">Create Job</button>
          </form>
        </section>
      </main>
      <Toaster />
    </div>
  );
}

export default Admin;
